package com.changing.party.service;

import lombok.extern.log4j.Log4j2;
import org.springframework.stereotype.Service;

import javax.imageio.ImageIO;
import java.awt.*;
import java.awt.image.BufferedImage;
import java.io.ByteArrayInputStream;
import java.io.ByteArrayOutputStream;
import java.io.IOException;
import java.nio.charset.StandardCharsets;
import java.util.Base64;

@Service
@Log4j2
public class ImageService {
    private static Font FONT_MSJH = new Font("C:\\Users\\Ziv\\Desktop\\1zSIGN fonts\\MSJH.TTF", 0, 17);

    public ByteArrayOutputStream imageAddText(String userName, String password) throws IOException {
        ByteArrayInputStream bais = new ByteArrayInputStream(Base64.getDecoder().decode(""));
        BufferedImage bufferedImage = ImageIO.read(bais);
        Graphics2D G = (Graphics2D) bufferedImage.getGraphics();
        G.setFont(FONT_MSJH);
        G.setColor(Color.BLACK);
        G.setRenderingHint(RenderingHints.KEY_ANTIALIASING, RenderingHints.VALUE_ANTIALIAS_ON);
        G.drawString(String.format("帳號: %s", userName), 60, 182);
        G.drawString(String.format("密碼: %s", password), 60, 202);
        ByteArrayOutputStream os = new ByteArrayOutputStream();
        ImageIO.write(bufferedImage, "png", os);
        return os;
    }

    public static void main(String[] args) throws IOException {
        ImageService imageService = new ImageService();
        System.out.println(new String(Base64.getEncoder().encode(imageService.imageAddText("Ziv", "ayzqpj").toByteArray()), StandardCharsets.UTF_8));
    }
}
